mapper.delete:
		DELETE FROM T_MEDU_MEDIA_AVD WHERE
		avd_id in

#{avdId,jdbcType=VARCHAR}

mapper.select:
select * from T_MEDU_MEDIA_AVD
and scene =#{scene,jdbcType=VARCHAR}
and (scene =#{scene,jdbcType=VARCHAR} or scene in ('', '1'))
and avd_title =#{avdTitle,jdbcType=VARCHAR}
and avd_num =#{avdNum,jdbcType=INTEGER}
and avd_state =#{avdState,jdbcType=VARCHAR}
and avd_begintime <=#{avdBegintime,jdbcType=VARCHAR}
and avd_endtime >=#{avdEndtime,jdbcType=VARCHAR}
and avd_linkhref =#{avdLinkhref,jdbcType=VARCHAR}
and avd_picurl =#{avdPicurl,jdbcType=VARCHAR}
and create_user_id =#{createUserId,jdbcType=VARCHAR}
and create_time =#{createTime,jdbcType=VARCHAR}
and update_user_id =#{updateUserId,jdbcType=VARCHAR}
and update_time =#{updateTime,jdbcType=VARCHAR}
and remark =#{remark,jdbcType=VARCHAR}
order by ${sidx} ${order}
order by avd_id desc

select * from T_MEDU_MEDIA_AVD t1
and t1.scene =#{scene,jdbcType=VARCHAR}
and (t1.scene =#{scene,jdbcType=VARCHAR} or t1.scene in ('', '1'))
and t1.avd_title =#{avdTitle,jdbcType=VARCHAR}
and t1.avd_num =#{avdNum,jdbcType=INTEGER}
and t1.avd_state =#{avdState,jdbcType=VARCHAR}
and t1.avd_begintime =#{avdBegintime,jdbcType=VARCHAR}
and t1.avd_endtime =#{avdEndtime,jdbcType=VARCHAR}
and t1.avd_linkhref =#{avdLinkhref,jdbcType=VARCHAR}
and t1.avd_picurl =#{avdPicurl,jdbcType=VARCHAR}
and t1.create_user_id =#{createUserId,jdbcType=VARCHAR}
and t1.create_time =#{createTime,jdbcType=VARCHAR}
and t1.update_user_id =#{updateUserId,jdbcType=VARCHAR}
and t1.update_time =#{updateTime,jdbcType=VARCHAR}
and t1.remark =#{remark,jdbcType=VARCHAR}
