mapper.update:
UPDATE T_SYS_RESOURCE SET parent_id = #{parentId,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, perms = #{perms,jdbcType=VARCHAR}, type = #{type,jdbcType=INTEGER}, icon = #{icon,jdbcType=VARCHAR}, order_num = #{orderNum,jdbcType=INTEGER}, ORG_ID = #{orgId,jdbcType=VARCHAR}, RESOURCE_CLASS = #{resourceClass,jdbcType=INTEGER}, REMARK = #{remark,jdbcType=VARCHAR}, DISPLAY = #{display,jdbcType=VARCHAR},
create_user_id = #{createUserId,jdbcType=VARCHAR},
create_time = #{createTime,jdbcType=VARCHAR},
update_user_id = #{updateUserId,jdbcType=VARCHAR},
update_time = #{updateTime,jdbcType=VARCHAR}
WHERE menu_id = #{menuId,jdbcType=VARCHAR}

mapper.select:
select m.*,(select p.name from t_sys_resource p where p.menu_id = m.parent_id) as parentName from t_sys_resource m where m.type <= 2
order by m.${sidx} ${order}
order by m.order_num asc

select m.*,(select p.name from t_sys_resource p where p.menu_id = m.parent_id) as parentName from t_sys_resource m
order by m.${sidx} ${order}
order by m.order_num asc

select IFNULL(max(order_num),0) from t_sys_resource where
