INSERT INTO `t_evgl_book_chapter` VALUES ('ab3f19b4265b4f69a2d4966bcf9511a3', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '40f5a8f1a55a45938a7b53395493cde9', 2, '工作环境', NULL, NULL, '<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-09/aa128b1f071a40e48208eedcedd4b201.jpg\" alt=\"\" width=\"741\" height=\"491\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/7498a3a8b1ff42d9937c0c3f563bb7e7.jpg\" alt=\"\" width=\"818\" height=\"614\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/f44c356b7bb046fda570a331f4e314e2.jpg\" alt=\"\" width=\"845\" height=\"634\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/bdb115152c1d4580a3242f97ab74713b.jpg\" alt=\"\" width=\"867\" height=\"650\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/c2147a4607aa44c78fa8ea67177b25d4.jpg\" alt=\"\" width=\"896\" height=\"672\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/27c944fe17234972b5f35918c9f7db91.jpg\" alt=\"\" width=\"956\" height=\"713\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/863ef112c7da4f1ea9ed4c95dd6006b8.jpg\" alt=\"\" width=\"913\" height=\"685\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/b0b8ccaf43884e0f95fd537a8049b8cf.jpg\" alt=\"\" width=\"889\" height=\"669\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-06-10/720e2c4869614828b30f7ee1019a0e48.jpg\" alt=\"\" width=\"900\" height=\"675\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-06-10/788176cbd7264420af9ea63159ce729d.jpg\" alt=\"\" width=\"900\" height=\"675\" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', NULL, 1, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('ac1980be9fd24bf5b9e817345c54ab3e', '536b1067bea54944bb48f309c5bf2b98', NULL, '4afef35ecab44a8691ad3d252c3c8161', NULL, '软件测试工程师', NULL, NULL, '<p><strong>软件测试工程师：企业招聘要求和需要掌握的技能</strong></p>\n<p>可以从事岗位：Web测试、APP测试、接口测试、安全性能测试、QA、QC等</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-14/63013b5315544676a627fc349086aaca.png\" width=\"681\" height=\"97\" /></p>\n<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td style=\"width: 100%;\"><strong>1、企业招聘要求 :有一年以上的WEB、APP、游戏项目测试经验；</strong></td>\n</tr>\n<tr>\n<td style=\"width: 100%;\">\n<p>1.1、正规统招全日制大专以上院校，计算机或相关专业毕业<br />1.2、踏实细致，吃苦耐劳，善于沟通，有一定语言表达和文字表达能力<br />1.3、足够的耐心，做事细心并有很强的责任心和团队精神<br />1.4、较强的办公文档编写能力，Office或WPS至少一种<br />1.5、至少熟练数据库MySQL或Oracle一种<br />1.6、维护测试环境、测试数据、测试文档，并负责项目文档的日常管理工作<br />1.7、熟悉测试生命周期，会使用Bugfree/Bugzilla/JIRA等测试管理工具；<br />1.8、能编写测试相关文档：测试用例、测试方案、测试报告，能对缺陷进行有效管理<br />1.9、熟悉基本linux命令，网络抓包、协议分析等。<br />1.10、能熟练使用LoadRunner或JMeter等测试工具进行性能测试<br />1.11、熟悉常用的渗透工具使用，漏洞扫描工具<br />1.12、具有独力安装使用自动测试与压力测试工具能力</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 100%;\"><strong>2、掌握的核心技术</strong></td>\n</tr>\n<tr>\n<td style=\"width: 100%;\">2.1、组织和实施软件测试项目，完成项目的功能测试，软件测试，黑盒测试，网页测试，手机测试、系统软件测试<br />2.2、数据库技术：熟悉Oracle、MySQL、Redis等数据库<br />2.3、会使用Bugfree/Bugzilla/JIRA/禅道等测试管理工具<br />2.4、熟练使用JMeter、Postman、LoadRunner等测试工具；<br />2.5、熟练掌握测试设计方法，熟悉等价类、边界值、正交等测试设计方法原理；<br />2.7、企业开发环境和部署环境，基本的linux操作、Maven、SVN、Git、Nginx应用等<br />2.8、良好的文档规范<br />2.9、强烈的责任心，积极主动的态度，较强的沟通、表达能力，良好的团队合作意识</td>\n</tr>\n<tr>\n<td style=\"width: 100%;\"><strong>3、岗位职责</strong></td>\n</tr>\n<tr>\n<td style=\"width: 100%;\">\n<p>3.1、理解负责测试的系统业务，进行需求分析和评审<br />3.2、制定测试方案和计划，编写测试用例和报告<br />3.3、根据测试流程和方法，按时完成分配的功能、性能、安全测试任务<br />3.4、维护测试环境、测试数据、测试文档，并负责项目文档的日常管理工作<br />3.5、具备良好的文档撰写能力，具有独立沟通，分析，解决问题的能力，有风险意识<br />3.6、思路清晰，善于思考，能独立分析和解决问题，责任心强，乐于承担工作压力<br />3.7、具有良好的学习能力、沟通能力，和团队管理能力<br />3.8、参与过一个或多个大型项目者优先考虑</p>\n</td>\n</tr>\n</tbody>\n</table>', NULL, 3, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-03-19 21:12:11', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
