INSERT INTO `t_evgl_book_chapter` VALUES ('c9f3c646a77d45969c27bd43c5d92a49', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '67587fe737054c55bfe3ec750770e9b4', 2, '任务实施', NULL, NULL, NULL, NULL, 3, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('cc8d70e7ac30422f969953aa7815ad75', '8487eebcb5cb4cfd9ff551c79046a449', NULL, 'a330bd8b63b34339832abaaf74721c84', 3, '数据库技术', NULL, NULL, '<div class=\"week-name\"><strong>第1-5天&nbsp; &nbsp;MySQL数据库</strong><hr /></div>\n<div class=\"content\">\n<div class=\"desc\">MySQL学习数据库基本操作。重点掌握SQL语句编写和SQL语句的优化。</div>\n<div class=\"line\">&nbsp;</div>\n<div class=\"black-title\">课程安排：</div>\n<div class=\"black-title\">1、MySQL数据库的安装和配置</div>\n<div class=\"text\">2、数据库的DDL操作：表创建、表的创建和修改等操作<br />3、数据表DML操作：增删改查等基本操作，<strong>如何构建百万级测试数据</strong><br />4、数据库的数字、日期时间、字符等常用函数使用和如何自定义函数<br />5、事务的ACID属性和隔离级别<br />6、数据库备份和恢复：SQL文件的导入和导出</div>\n<div class=\"text\">7、聚合函数</div>\n<div class=\"text\">8、分组</div>\n<div class=\"text\">9、多表查询、子查询</div>\n<div class=\"text\">10、查询重复数据并去重</div>\n<div class=\"text\">11、存储过程</div>\n<div class=\"text\">12、触发器</div>\n<div class=\"text\">13、用户和权限</div>\n</div>\n<div class=\"content\">\n<div class=\"text\">&nbsp;</div>\n<div class=\"text\">\n<div class=\"week-name\"><strong>第6-10天&nbsp; &nbsp;Oracle数据库及数据库连接池</strong></div>\n<div class=\"content\">\n<div class=\"desc\">在实际开发中，像金融银行系统、物流系统会涉及到Oracle数据库技术应用</div>\n<div class=\"black-title\">课程安排：</div>\n<div class=\"text\">1、Windows下安装oracle11g<br />2、Oracle服务及监听器启动及停止<br />3、限制客户端连接到数据库服务器<br />4、Oracle常用的数据类型<br />5、创建表空间和用户<br />6、数据库导入导出<br />7、如何新建、修改、删除表<br />8、如何创建分区表<br />9、如何插入数据<br />10、如何修改数据<br />11、如何删除数据<br />12、如何查询数据和分页查询数据<br />13、如何递归查询数据<br />14、Oracle常用函数<br />15、Oracle视图<br />16、Oracle角色和权限控制<br />17、Oracle任务调度JOB<br />18、Oracle索引<br />19、如何创建Oracle只读用户<br />20、Oracle同义词<br />21、Oracle触发器<br />22、如何查看Oracle连接数</div>\n</div>\n</div>\n</div>\n<div class=\"week-name\"><strong>第11-12天&nbsp; &nbsp;Linux环境搭建与Redis应用</strong></div>\n<div class=\"content\">\n<div class=\"desc\">在实际开发中，项目最终要被部署到Linux环境下，使用Redis进行数据缓存，提高访问速度。</div>\n<div class=\"line\">Redis分布式缓存集群</div>\n<div class=\"black-title\">课程安排：</div>\n<div class=\"text\">1、CentOS 7系统、以及客户端工具Xshell和XFtp的下载和安装<br />2、Linux系统目录结构和常用命令<br />3、文本编辑工具vim<br />4、使用tar命令进行文件压缩和解压缩<br />5、使用yum进行软件安装卸载<br />6、Linux的网络设置<br />7、Linux下安装、部署并运行Web应用<br />8、Redis下载安装、基本配置和常用命令<br />9、使用Jedis对Redis进行操作</div>\n</div>', NULL, 2, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('d1714be87966419fa6c5624f1b3d1dbb', '536b1067bea54944bb48f309c5bf2b98', NULL, 'ac1980be9fd24bf5b9e817345c54ab3e', NULL, '阶段四 性能测试项目实战', NULL, NULL, '<div class=\"stage-section js-areatop js-section-0\">\n<div class=\"week-name\">\n<div class=\"stage-section js-areatop js-section-0\">\n<div class=\"week-name\"><strong>阶段一：Jmeter性能测试-基础篇</strong></div>\n<div class=\"week-name\"><strong>以国学教育项目为驱动，涉及软件开发流程中的各个工作环节，全面真实的web、app项目测试实战。</strong></div>\n<div class=\"week-name\"><strong>的学习模式设计，从软件测试理论基础</strong></div>\n<div class=\"week-name\"><hr /></div>\n<div class=\"content\">\n<div class=\"black-title\"><strong>课程安排：</strong></div>\n<div class=\"text\"><strong>1、性能测试入门</strong></div>\n<div class=\"text\">性能测试概念与理解、性能测试工具、企业性能测试分析、行业性能测试分析<br /><strong>2、性能测试主要指标</strong><br />TPS、并发用户数、平均响应时间、吞吐量、资源利用率、性能测试工具介绍、性能测试前提条件</div>\n<div class=\"text\">3、本地快速安装Jmeter4.x</div>\n<div class=\"text\">4、Jmeter目录文件讲解</div>\n<div class=\"text\">5、&nbsp;Jmeter核心组件讲解和实操</div>\n<div class=\"text\">6、JMeter4.x基础知识讲解和压测实操</div>\n<div class=\"text\">7、Jmeter基本介绍和使用场景</div>\n<div class=\"text\">8、Jmeter的断言基本使用</div>\n<div class=\"text\">9、Jmeter实战之压测结果聚合报告分析</div>\n<div class=\"text\">10、Jmeter压测脚本JMX讲解</div>\n<div class=\"text\">11、Jmeter用户自定义变量实战</div>\n<div class=\"text\">12、Jmeter实战之CSV可变参数压测</div>\n<div class=\"text\">13、CSV文件多参数使用</div>\n<div class=\"text\">14、Jmeter压测实战之JDBC request压测Mysql讲解</div>\n<div class=\"text\">15、Jmeter压测实战之JDBC request压测Mysql, select语句</div>\n<div class=\"text\">16、分布式压测介绍</div>\n<div class=\"text\">17、Jmeter分布式压测原理</div>\n<div class=\"text\">18、高级篇之阿里云Linux服务器压测接口实战</div>\n<div class=\"text\">19、阿里云Linux服务器安装Jmeter 4.0</div>\n<div class=\"text\">20、Jmeter非GUI界面 参数讲解</div>\n<div class=\"text\">21、项目实战之阿里云Linux服务器下非GUI执行jmeter压测</div>\n<div class=\"text\">22、阿里云Linux服务器 Jmeter压测实战之jtl文件生成和查看</div>\n<div class=\"text\">23、Jmeter压测接口的性能优化</div>\n<div class=\"text\">24、项目实战之Jmeter压测生成多维度图形化HTML测试报告</div>\n<div class=\"text\">25、Jmeter图形化HTML压测报告dashboard讲解</div>\n<div class=\"text\">26、Jmeter图形化HTML压测报告Charts报表讲解</div>\n<div class=\"text\">27、jmeter压测常见问题处理</div>\n<div class=\"text\">28、高级篇之Jmeter压测课程总结和架构浅析</div>\n<div class=\"text\">&nbsp;</div>\n</div>\n<div class=\"content\">\n<div class=\"text\">&nbsp;</div>\n</div>\n</div>\n<div class=\"stage-section js-areatop js-section-1\">\n<div class=\"top\">&nbsp;</div>\n</div>\n</div>\n</div>', NULL, 4, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-03-23 15:06:44', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('d38f99336f9941618a7cb56e3b7d1b3f', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '91bdccec7ae14f92ad63377c1dd880d3', 2, '常用工具见云盘', NULL, NULL, '<p>企业工作用的软件清单</p>\n<table style=\"border-collapse: collapse; width: 100%; height: 400px;\" border=\"1\">\n<tbody>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">序号</td>\n<td style=\"width: 40.2542%; height: 20px;\">软件名称</td>\n<td style=\"width: 25%; height: 20px;\">备注说明</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">1</td>\n<td style=\"width: 40.2542%; height: 20px;\">微信PC端</td>\n<td style=\"width: 25%; height: 20px;\">&nbsp;</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">2</td>\n<td style=\"width: 40.2542%; height: 20px;\">Foxmail</td>\n<td style=\"width: 25%; height: 20px;\">收发邮件</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">3</td>\n<td style=\"width: 40.2542%; height: 20px;\">FastStone Image Viewer</td>\n<td style=\"width: 25%; height: 20px;\">看图软件</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">4</td>\n<td style=\"width: 40.2542%; height: 20px;\">Axure RP 9</td>\n<td style=\"width: 25%; height: 20px;\">交互性原型设计工具</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">5</td>\n<td style=\"width: 40.2542%; height: 20px;\">draw.io/亿图</td>\n<td style=\"width: 25%; height: 20px;\">绘图软件</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">6</td>\n<td style=\"width: 40.2542%; height: 20px;\">Bandicam</td>\n<td style=\"width: 25%; height: 20px;\">录屏软件</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\">7</td>\n<td style=\"width: 40.2542%; height: 20px;\">sublime</td>\n<td style=\"width: 25%; height: 20px;\">记事本：编辑器</td>\n</tr>\n<tr style=\"height: 20px;\">\n<td style=\"width: 9.74576%; height: 20px;\"