INSERT INTO `t_evgl_book_chapter` VALUES ('e54c561b7dc9404fa8852896aecb47a7', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '8487eebcb5cb4cfd9ff551c79046a449', 1, '体验课-户外通旅游项目实战', NULL, NULL, '<p><strong>体验课的目标：</strong></p>\n<p>1、让学生熟知企业是如何开发软件项目的整个流程</p>\n<p>2、快速安装和部署MySQL数据库</p>\n<p>3、快速安装和部署Tomcat应用服务器</p>\n<p>4、快速部署项目</p>\n<p>5、访问项目，系统试运行并测试</p>\n<p><a href=\"https://outdoor.creatorblue.com/admin/login.html\" target=\"_blank\" rel=\"noopener\">https://outdoor.creatorblue.com/admin/login.html</a></p>\n<p>登录账号/密码：admin/123456</p>\n<p>&nbsp;</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-20/87ee80f190e6431c854ad34c442c856b.png\" width=\"888\" height=\"481\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-20/7a741278a83549c3aeb66b59117fa129.png\" width=\"347\" height=\"752\" /><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-20/ad9df90f59f145a3882f8ffa4c8ff208.png\" width=\"376\" height=\"815\" /></p>', NULL, 7, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('e5a81b7251724dbcae9dc997a8ab93af', '536b1067bea54944bb48f309c5bf2b98', NULL, 'c0f3761816594137999dc63824c7f8b2', NULL, '前端基础入门', NULL, NULL, '<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\"><strong>HTML5基础语法与标签</strong></span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">本周是入门前端的第一步，学会IDE插件安装和使用，掌握H5语法和基础标签的应用，学完本周内容可以进行简单的页面结构搭建。</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、了解前端行业发展</span></p>\n<p><span style=\"font-size: 14px;\">2、了解HTML5对于网页的意义</span></p>\n<p><span style=\"font-size: 14px;\">3、学习常用标签/属性，进行结构搭建</span></p>\n<p><span style=\"font-size: 14px;\">4、掌握无序列表/有序列表/自定义列表</span></p>\n<p><span style=\"font-size: 14px;\">5、学习表单标签/属性，能创建常见表单结构</span></p>\n<p><span style=\"font-size: 14px;\">6、掌握标签嵌套规则/行内元素/块元素的使用</span></p>\n<p><span style=\"font-size: 14px;\">7、学习VSCode开发工具使用，能创建简单网页</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\"><strong>CSS3基础语法与盒模型</strong></span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">CSS3是网页的\"美容师\"，本周开始学习CSS3样式，掌握语法结构、选择器、盒子模型、文本和字体样式设置，能够对网页进行简单的样式开发。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、掌握CSS3基本语法和使用</span></p>\n<p><span style=\"font-size: 14px;\">2、掌握CSS3各类选择器/样式属性，快速进行样式设置</span></p>\n<p><span style=\"font-size: 14px;\">3、学习文本、字体等常见属性</span></p>\n<p><span style=\"font-size: 14px;\">4、盒子模型核心知识讲解，深入理解原理</span></p>\n<p><span style=\"font-size: 14px;\">5、掌握行内元素和块级元素设置与转换</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">CSS3浮动定位与背景样式</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">简单的样式开发还不够，通过掌握浮动、定位、边框、背景样式以及2D&amp;3D转换，以及布局技巧。能够进行特殊图形绘制，开发更美观的网页效果。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、浮动与定位核心知识，灵活运用实现网页布局</span></p>\n<p><span style=\"font-size: 14px;\">2、常见布局方法，解决布局常见问题</span></p>\n<p><span style=\"font-size: 14px;\">3、如何绘制圆角边框/阴影框/图片边框等特殊图形</span></p>\n<p><span style=\"font-size: 14px;\">4、掌握元素扭曲/移位/旋转，更自由的装饰HTML</span></p>\n<p><span style=\"font-size: 14px;\">5、BFC规范和浏览器差异</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">CSS3动画与天逸国际名车首页开发实战</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">CSS3也能实现炫酷的网页动态效果，结合企业级上线&ldquo;旅游网&rdquo;项目，综合运用前面所学的知识，完成穷游首页排版布局和CSS3动画特效开发。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、大项目：结合H5/CSS3，完成PC端天逸国际名车首页布局开发</span></p>\n<p><span style=\"font-size: 14px;\">2、掌握不同布局结构与技巧</span></p>\n<p><span style=\"font-size: 14px;\">3、掌握过渡的使用与缓动效果，实现常见小案例</span></p>\n<p><span style=\"font-size: 14px;\">4、运用动画效果，实现炫酷动画效果</span></p>\n<p><span style=\"font-size: 14px;\">5、大作业：天逸国际名车网页布局开发</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">JS基础语法与表达式</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">JS是网页编程的第一步，本周开始学习JS编程语言，掌握基础语法结构、变量声明和命名规则、数据类型、表达式和操作符，学完本周内容可以进行简单的JS代码编写。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、掌握JS基本语法使用</span></p>\n<p><span style=\"font-size: 14px;\">2、掌握JS变量声明与提升的机理</span></p>\n<p><span style=\"font-size: 14px;\">3、掌握JS运算符操作与表达式</span></p>\n<p><span style=\"font-size: 14px;\">4、学习基本数据类型和复杂数据类型使用</span></p>\n<p><span style=\"font-size: 14px;\">5、深入理解数据类型转换与检测</span></p>\n<p><span style=\"font-size: 14px;\">6、小案例：计算器和表达式综合运用</span></p>\n<div class=\"step_content js-fold-content\">\n<div class=\"white-box\">\n<div class=\"week_content_area\">\n<div class=\"week_content_scroll js-tablist-content\">\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">JS流程控制语句与数组</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">本周继续深入学习JS编程语言，掌握条件分支语句、循环语句和数组，学完本周内容可以用简洁的代码实现强大功能。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、掌握if，if elseif，switch等条件分支语句使用</span></p>\n<p><span style=\"font-size: 14px;\">2、掌握for、while、do while循环语句使用</span></p>\n<p><span style=\"font-size: 14px;\">3、掌握数组基本使用和常用方法</span></p>\n<p><span style=\"font-size: 14px;\">4、break和continue语句的特点及应用</span></p>\n<p><span style=\"font-size: 14px;\">5、运用数组知识，学习基本算法</span></p>\n<p><span style=\"font-size: 14px;\">6、使用简单的逻辑实现复杂业务逻辑</span></p>\n<p><span style=\"font-size: 14px;\">7、小案例：常见算法综合运用</span></p>\n</div>\n</div>\n</div>\n</div>\n<div class=\"step_title js-fold-trigger \">\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">JS函数与DOM</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">如想实现更炫的动态效果，那么操控网页元素很重要，通过掌握函数编程、DOM操作、事件以及BOM对象，让你能实现典型的触发和动态交互效果。</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、掌握DOM操作和DOM事件</span></p>\n<p><span style=\"font-size: 14px;\">2、掌握函数基础与函数高级应用</span></p>\n<p><span style=\"font-size: 14px;\">3、掌握BOM浏览器对象模型，与浏览器&ldquo;对话&rdquo;</span></p>\n<p><span style=\"font-size: 14px;\">4、掌握函数封装，提升编码质量</span></p>\n<p><span style=\"font-size: 14px;\">5、小案例：有趣的动画效果开发</span></p>\n</div>\n<div class=\"step_title js-fold-trigger \">\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">面向对象</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">面向对象是开发中非常重要的思想，在本周我们将开始运用面向对象思想进行程序开发，编写高质量代码，解决企业级编程协同问题。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、学习this规则与使用</span></p>\n<p><span style=\"font-size: 14px;\">2、掌握构造函数概念以及创建、调用与使用</span></p>\n<p><span style=\"font-size: 14px;\">3、理解原型和原型链的关系与运用</span></p>\n<p><span style=\"font-size: 14px;\">4、闭包和作用域应用</span></p>\n<p><span style=\"font-size: 14px;\">5、熟练使用面向对象思想进行DOM编程</span></p>\n<p><span style=\"font-size: 14px;\">6、掌握JS模块化编程方式，编写高质量代码</span></p>\n<p><span style=\"font-size: 14px;\">7、掌握模块化开发技巧，解决企业级编程协同问题</span></p>\n<p><span style=\"font-size: 14px;\">8、小案例：俄罗斯方块游戏开发</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">项目实战：天逸国际名车JS特效开发</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">实战出真知，本周综合运用H5、CSS3和JS所学知识，从0到1完成一个具备CSS3动画和JS特效的多特效旅游网页。掌握企业常见的网页开发方式和各类特效实现方案。</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、结合H5/CSS3/JS，完成PC端天逸国际名车首页特效开发</span></p>\n<p><span style=\"font-size: 14px;\">2、项目动画效果分析，帮助更快梳理思路</span></p>\n<p><span style=\"font-size: 14px;\">3、实现典型JS特效效果：Banner轮播图、返回顶部动画、垂直菜单</span></p>\n<p><span style=\"font-size: 14px;\">4、学习正则表达式，完成常见手机号、邮箱、姓名等功能验证</span></p>\n<p><span style=\"font-size: 14px;\">3、大作业：天逸国际名车网页JS特效开发</span></p>\n</div>', NULL, 2, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-03-23 10:46:43', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('e8d570ab8f9f4205a2e2d506a2fc441d', '536b1067bea54944bb48f309c5bf2b98', NULL, '83bcb90aed2849f19728665a42bcd689', NULL, '任务知识点', NULL, NULL, '<h3><strong>多线程插入（单表）</strong></h3>\n<p>做插入操作的时候，整体时间的分配是这样的：</p>\n<ol>\n<li>链接耗时 （30%）</li>\n<li>发送query到服务器 （20%）</li>\n<li>解析query （20%）</li>\n<li>插入操作 （10% * 词条数目）</li>\n<li>插入index （10% * Index的数目）</li>\n<li>关闭链接 （10%）</li>\n</ol>\n<p>从这里可以看出来，真正耗时的不是操作，而是链接，解析的过程。</p>\n<p>MySQL插入数据在写阶段是独占的，但是插入一条数据仍然需要解析、计算、最后才进行写处理，比如要给每一条记录分配自增id，校验主键唯一键属性，或者其他一些逻辑处理，都是需要计算的，所以说多线程能够提高效率。</p>\n<h3>多线程插入(多表)</h3>\n<p>分区分表后使用多线程插入。</p>\n<h3>预处理SQL</h3>\n<section>普通SQL，即使用Statement接口执行SQL</section>\n<section>预处理SQL，即使用PreparedStatement接口执行SQL</section>\n<p>使用PreparedStatement接口允许数据库预编译SQL语句，以后只需传入参数，避免了数据库每次都编译SQL语句，因此性能更好。</p>\n<pre class=\"language-javascript\"><code>String sql = \"insert into testdb.tuser (name, remark, createtime, updatetime) values (?, ?, ?, ?)\";\nfor (int i = 0; i &lt; m; i++) {\n    //从池中获取连接\n    Connection conn = myBroker.getConnection();\n    PreparedStatement pstmt = conn.prepareStatement(sql);\n    for (int k = 0; k &lt; n; k++) {\n            pstmt.setString(1, RandomToolkit.generateString(12));\n            pstmt.setString(2, RandomToolkit.generateString(24));\n            pstmt.setDate(3, new Date(System.currentTimeMillis()));\n            pstmt.setDate(4, new Date(System.currentTimeMillis()));\n            //加入批处理\n            pstmt.addBatch();\n    }\n    pstmt.executeBatch();    //执行批处理\n    pstmt.close();\n    myBroker.freeConnection(conn); //连接归池\n}</code></pre>\n<h3>多值插入SQL</h3>\n<ul class=\"list-paddingleft-2\">\n<li>\n<section>普通插入SQL：INSERT INTO TBL_TEST (id) VALUES(1)</section>\n</li>\n<li>\n<section>多值插入SQL：INSERT INTO TBL_TEST (id) VALUES (1), (2), (3)</section>\n</li>\n</ul>\n<p>使用多值插入SQL，SQL语句的总长度减少，即减少了网络IO，同时也降低了连接次数，数据库一次SQL解析，能够插入多条数据。</p>\n<h3>事务(N条提交一次)</h3>\n<p>在一个事务中提交大量INSERT语句可以提高性能。</p>\n<p>1、将表的存储引擎修改为myisam 2、将 sql 拼接成字符串，每 1000 条左右提交事务。</p>\n<pre class=\"language-javascript\"><code>/// &lt;summary&gt;\n        /// 执行多条SQL语句，实现数据库事务。\n        /// &lt;/summary&gt;mysql数据库\n        /// &lt;param name=\"SQLStringList\"&gt;多条SQL语句&lt;/param&gt;\n        public void ExecuteSqlTran(List&lt;string&gt; SQLStringList)\n        {\n            using (MySqlConnection conn = new MySqlConnection(connectionString))\n            {\n                if (DBVariable.flag)\n                {\n                    conn.Open();\n                    MySqlCommand cmd = new MySqlCommand();\n                    cmd.Connection = conn;\n                    MySqlTransaction tx = conn.BeginTransaction();\n                    cmd.Transaction = tx;\n                    try\n                    {\n                        for (int n = 0; n &lt; SQLStringList.Count; n++)\n                        {\n                            string strsql = SQLStringList[n].ToString();\n                            if (strsql.Trim().Length &gt; 1)\n                            {\n                                cmd.CommandText = strsql;\n                                cmd.ExecuteNonQuery();\n                            }\n                            //后来加上的\n                            if (n &gt; 0 &amp;&amp; (n % 1000 == 0 || n == SQLStringList.Count - 1))\n                            {\n                                tx.Commit();\n                                tx = conn.BeginTransaction();\n                            }\n                        }\n                        //tx.Commit();//原来一次性提交\n                    }\n                    catch (System.Data.SqlClient.SqlException E)\n                    {\n                        tx.Rollback();\n                        throw new Exception(E.Message);\n                    }\n                }\n            }\n        }</code></pre>', NULL, 4, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-05-29 11:40:32', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('eadeb0b59a6343aa9d41ed55a769d859', '8487eebcb5cb4cfd9ff551c79046a449', NULL, 'e54c561b7dc9404fa8852896aecb47a7', 2, '实训八 部署上线', NULL, NULL, '<p><strong>部署准备</strong></p>\n<p>1、编写系统部署手册：版本变更说明，准备好部署的程序和数据库脚本、部署拓扑图等</p>\n<p>《传奇户外通微信小程序项目部署方案说明书_V1.doc》</p>\n<p>2、安装好环境：数据库服务器、应用服务器、代理服务器（可选）</p>\n<p><strong>交付产物</strong></p>\n<p><strong>《系统部署手册》</strong></p>\n<p><strong>《系统部署版本说明》</strong></p>\n<p><strong>例如：</strong></p>\n<p><strong>1、硬件环境</strong></p>\n<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\" width=\"100%\">\n<tbody>\n<tr>\n<td width=\"56\">\n<p><strong>序号</strong></p>\n</td>\n<td width=\"123\">\n<p><strong>服务器用途</strong></p>\n</td>\n<td width=\"255\">\n<p><strong>硬件配置</strong></p>\n</td>\n<td width=\"66\">\n<p><strong>IP</strong><strong>地址</strong></p>\n</td>\n<td width=\"85\">\n<p><strong>备注说明</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\"56\">\n<p>1</p>\n</td>\n<td width=\"123\">\n<p><strong>数据库服务器</strong></p>\n</td>\n<td width=\"255\">\n<p>CPU：PIV1.6G以上</p>\n<p>内存：256以上</p>\n<p>硬盘：40G以上</p>\n<p>VGA：分辨率800*600或更高</p>\n<p>网卡：100M以上</p>\n<p>其他：键盘、鼠标</p>\n</td>\n<td width=\"66\">\n<p>192.168.0.1</p>\n<p>3308</p>\n</td>\n<td width=\"85\">\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\"56\">\n<p>2</p>\n</td>\n<td width=\"123\">\n<p><strong>应用服务器</strong></p>\n</td>\n<td width=\"255\">\n<p>CPU：PIV1.6G以上</p>\n<p>内存：256以上</p>\n<p>硬盘：40G以上</p>\n<p>VGA：分辨率800*600或更高</p>\n<p>网卡：100M以上</p>\n<p>其他：键盘、鼠标</p>\n<p>&nbsp;</p>\n</td>\n<td width=\"66\">\n<p>192.168.0.2</p>\n<p>8080</p>\n</td>\n<td width=\"85\">\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\"56\">\n<p>3</p>\n</td>\n<td width=\"123\">\n<p><strong>Nginx服务器</strong></p>\n</td>\n<td width=\"255\">\n<p>CPU：PIV1.6G以上</p>\n<p>内存：256以上</p>\n<p>硬盘：40G以上</p>\n<p>VGA：分辨率800*600或更高</p>\n<p>网卡：100M以上</p>\n<p>其他：键盘、鼠标</p>\n<p>&nbsp;</p>\n</td>\n<td width=\"66\">\n<p>192.168.0.3</p>\n<p>80</p>\n</td>\n<td width=\"85\">\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>2、网络环境</strong></p>\n<p><strong>2.1.说明</strong></p>\n<p>本系统需要与运行在单位局域网上，要求服务器、客户端计算机连接在此网络上。建议配置100M网络速度。</p>\n<p><strong>2.2.网络拓扑图</strong></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-21/b07b7c3da8bf438ea3b0b6dd6e06cdeb.png\" width=\"794\" height=\"356\" /></p>\n<p><strong>3、软件环境</strong></p>\n<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td width=\"74\">\n<p><strong>序号</strong></p>\n</td>\n<td width=\"190\">\n<p><strong>工具名称</strong></p>\n</td>\n<td width=\"123\">\n<p><strong>版本号</strong></p>\n</td>\n<td width=\"198\">\n<p><strong>软件分类</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\"74\">\n<p>1</p>\n</td>\n<td width=\"190\">\n<p>MySQL</p>\n</td>\n<td width=\"123\">\n<p>8.2</p>\n</td>\n<td width=\"198\">\n<p>服务端</p>\n</td>\n</tr>\n<tr>\n<td width=\"74\">\n<p>2</p>\n</td>\n<td width=\"190\">\n<p>TOMCAT</p>\n</td>\n<td width=\"123\">\n<p>9.x</p>\n</td>\n<td width=\"198\">\n<p>服务端</p>\n</td>\n</tr>\n<tr>\n<td width=\"74\">\n<p>3</p>\n</td>\n<td width=\"190\">\n<p>jdk</p>\n</td>\n<td width=\"123\">\n<p>1.8</p>\n</td>\n<td width=\"198\">\n<p>服务端</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>', NULL, 9, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('ef0671cf493648728b8130c39433349e', '536b1067bea54944bb48f309c5bf2b98', NULL, 'd454480636cf49a39b6d33becd082b6f', NULL, '教学包设计', NULL, NULL, '<p>整个教学包的设计包括如下几部分：教材设计、讲义PPT设计、教学大纲设计、视频设计等。</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-22/d2e74cbafec644d0aeaaac7daad19f87.png\" /></p>\n<p>说明：</p>\n<p>1、公共部分：教学材料以就业为导向，根据合作企业的用人需求逆向制定教学大纲</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-22/afcd8a8eaeac4ee59d00a1f2b95c2e15.png\" /></p>\n<p>2、第1章：章节名称</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-22/7ac3e97cd844497f8778c165bc76a6a8.png\" /></p>', NULL, 2, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-03-30 16:21:53', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('f0071ba5466942aaa6c127ab6144da01', '8487eebcb5cb4cfd9ff551c79046a449', NULL, 'e54c561b7dc9404fa8852896aecb47a7', 2, '实训五 数据库设计', NULL, NULL, '<p><img style=\"max-width: 100%; -webkit-text-stroke-width: 0px;\" title=\"1551180382309096290.png\" src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-16/b37a9519f3494fb2bacecd8fcb9398f1.png\" alt=\"image.png\" width=\"820\" height=\"301\" /></p>\n<p>1、数据库采用MySQL8.0</p>\n<p>2、建模工具采用PowerDesinger</p>\n<p>3、数据库文档生成工具</p>', NULL, 6, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('f1b81e530a6b4c1b9cbb1c6e5ec31ba2', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '91bdccec7ae14f92ad63377c1dd880d3', 2, '操作系统安装', NULL, NULL, '<p>Windows 硬盘安装很简单：</p>\n<p>方法一：解压后，以管理员身份运行setup.exe</p>\n<p><img style=\"width: 800px;\" src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-05-21/9016c77523ee47b09372bd5556076dfa.png\" /></p>\n<p>方法二：加载到虚拟光盘中运行</p>\n<p><img style=\"width: 800px;\" src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-05-21/e1cd819fd3a843afb9c1309e439af75e.png\" /></p>\n<p>安装过程中注意几点：</p>\n<p>1、备份好C盘自己的文件：例如图片，视频、文档等 迁移到其他非系统盘：例如D盘</p>\n<p>2、安装过程中提示是保留文件和应用程序，则原来安装的程序，在重新安装操作系统后，还可以使用，一般建议全新安装，不保留C盘任何文件和应用程序</p>\n<p><strong>安装完成后，清理C盘历史版本的WINDOW</strong></p>\n<p><strong>点C盘右键属性-----</strong></p>\n<p><img style=\"width: 800px;\" src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-05-21/f2c7193ff5af44ed97311a31bfb775b3.png\" /></p>\n<p>勾选所有的文件，点删除即可</p>\n<p>一般至少能删除旧的操作系统文件几个G</p>\n<p>&nbsp;</p>', NULL, 1, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('f2d532c87bb241bfb1cbd8e6bb6fbac7', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '40f5a8f1a55a45938a7b53395493cde9', 2, '合作企业', NULL, NULL, '<p>推出成本可控的定时、定制、定量的&ldquo;精准IT人才&rdquo;服务，提出了从人才招聘、定制培养到 人才输送的一揽子IT人力资源整体解决方案，帮助企业招到人、培训好人、用好人，协助企业走出优秀人才短缺的困境。</p>\n<p>学有所成、学有所用，莘莘学子选择了创蓝教育，五年来取得丰硕成果。</p>\n<p>战略合作软件企业累积达100+</p>\n<p>成功向合作企业输送学员已超400+</p>\n<p><strong>部分合作企业展示：</strong></p>\n<p>&nbsp;</p>', NULL, 2, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('f4332ddb952d47a6959f9872459ed788', '536b1067bea54944bb48f309c5bf2b98', NULL, 'c0f3761816594137999dc63824c7f8b2', NULL, 'Vue专题', NULL, NULL, '<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\"><strong>小程序基础与户外通小程序实战</strong></span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">本周开始学习爆火的轻应用开发技术&mdash;&mdash;小程序 。通过对小程序知识的学习，能够独立开发企业级小程序，拥有解决主流小程序功能需求的能力。</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、大项目：结合Flex/Template模板，开发户外通小程序首页/活动页/热门排行页</span></p>\n<p><span style=\"font-size: 14px;\">2、了解小程序注册流程/注册方式/注册主体</span></p>\n<p><span style=\"font-size: 14px;\">3、小程序的全局生命周期和页面级生命周期</span></p>\n<p><span style=\"font-size: 14px;\">4、小程序基础语法与常用API</span></p>\n<p><span style=\"font-size: 14px;\">5、小程序开发工具的调试办法</span></p>\n<p><span style=\"font-size: 14px;\">6、小程序多页面下的开发工作流及目录结构</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">Vue.js基础语法与博客网实战</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">Vue.js是企业开发热门框架之一 。通过对Vue.js基础和项目的学习，具备构建企业级项目和解决核心业务问题的能力，快速接入企业项目。</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、大作业：运用Vuejs组件完成博客网项目常见功能开发</span></p>\n<p><span style=\"font-size: 14px;\">2、理解Vuejs的开发理念与内部原理</span></p>\n<p><span style=\"font-size: 14px;\">3、Vuejs的基本使用及Vuejs组件库进行项目开发</span></p>\n<p><span style=\"font-size: 14px;\">4、使用React封装项目中用到组件，实现复用</span></p>\n<p><span style=\"font-size: 14px;\">5、Vuejs项目中常见问题的解决方案</span></p>\n<p><span style=\"font-size: 14px;\">6、声明式编程的思想与组件化开发的思想</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">Sass基础与Vue.js基础语法</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">Vue.js是企业开发另一热门框架，易用、灵活、高效，对于初学者很友好。本周通过对Vue.js基础和Sass布局技术的学习，为后续的全栈项目开发打下基础。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、大作业：订单列表和购物车列表页</span></p>\n<p><span style=\"font-size: 14px;\">2、掌握Webpack项目构建配置</span></p>\n<p><span style=\"font-size: 14px;\">3、掌握基础语法与常见API</span></p>\n<p><span style=\"font-size: 14px;\">4、组件的使用及组件的生命周期</span></p>\n<p><span style=\"font-size: 14px;\">5、Axios网络请求及路由使用</span></p>\n<p><span style=\"font-size: 14px;\">6、Vuex的引入、应用场景及项目中的实现</span></p>\n<p><span style=\"font-size: 14px;\">7、使用Vue.js技术栈进行项目开发的方式方法</span></p>\n<p><span style=\"font-size: 14px;\">8、掌握Sass布局基础</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">项目实战：Vue.js仿布道师全栈项目前端开发（上）</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">从本周开始进入到电商全栈项目开发，首先使用Vue.js前端实战的第一部分首页和商家详情页，还原实际开发流程，综合运用所学知识，完成页面开发，掌握开发中常见问题解决技巧。</span></p>\n<div class=\"line\">&nbsp;</div>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、综合运用Sass和Vue.js完成登录页/注册页/首页/商家详情页</span></p>\n<p><span style=\"font-size: 14px;\">2、使用vue-cli4.0创建开发环境</span></p>\n<p><span style=\"font-size: 14px;\">3、使用axios发送Mock请求</span></p>\n<p><span style=\"font-size: 14px;\">4、使用路由守卫实现基础登陆校验功能</span></p>\n<p><span style=\"font-size: 14px;\">5、动态路由/异步路由与组件拆分复用</span></p>\n<p><span style=\"font-size: 14px;\">6、首页/商家详情页数据渲染</span></p>\n<p><span style=\"font-size: 14px;\">7、Vue.js项目中常见问题的解决方案</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">项目实战：Vue.js仿京东到家电商全栈项目前端（下）</span></h6>\n<p class=\"week_item_normal_text\"><span style=\"font-size: 14px;\">Vue.js前端项目实战的第二部分，实现电商核心业务订单和地址管理模块，掌握开发中常见问题解决技巧，真实数据访问，开发完整的Vue.js电商前端项目。</span></p>\n<h6 class=\"week_item_title\"><span style=\"font-size: 14px;\">课程安排：</span></h6>\n<p><span style=\"font-size: 14px;\">1、综合运用Sass和Vue.js完成订单提交页/地址列表页/地址编辑/地址新建页</span></p>\n<p><span style=\"font-size: 14px;\">2、使用axios发送Mock 请求</span></p>\n<p><span style=\"font-size: 14px;\">3、动态路由/异步路由与组件拆分复用</span></p>\n<p><span style=\"font-size: 14px;\">4、订单提交页/地址列表页数据渲染</span></p>\n<p><span style=\"font-size: 14px;\">5、Vue.js项目中常见问题的解决方案</span></p>\n<p><span style=\"font-size: 14px;\">6、步骤大作业：完成订单列表页和购物车列表页开发</span></p>', NULL, 4, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-03-23 10:55:49', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('f518fa85fea7453cb71f5721de3a3583', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '8487eebcb5cb4cfd9ff551c79046a449', 1, '创蓝教育简介', NULL, NULL, '<p>&nbsp;&nbsp;&nbsp;&nbsp; <strong>创蓝教育</strong>是一家专注于<strong>IT</strong>职业教育的中高端O2O教育品牌，以&ldquo;立工匠精神，育软件英才&rdquo;为使命，与北上广深，长沙武汉上百家软件企业合作，创立&ldquo;订单实训&rdquo; 的人才培养模式，推出成本可控的定时、定制、定量的&ldquo;精准IT人才服务&rdquo;，旨在为合作企业输送合适的软件人才，为高校大学生提升IT职业技能，实现精准就业！</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/2a935e43a4a8492a89b08006f467f71e.jpg\" alt=\"\" width=\"800\" height=\"470\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-14/9d5e3542858b4294b96a2a2e9a0cc0d1.png\" width=\"783\" height=\"443\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-14/243849cea80640f7ae3240d3abf7c087.png\" width=\"761\" height=\"427\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-14/3e5fdc3cef2a48aea7252b8acbe6214e.png\" width=\"765\" height=\"420\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-06-10/ef9f8edf6d62466abbaf657732c6dc92.jpg\" alt=\"\" width=\"784\" height=\"588\" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', NULL, 0, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('f551360bca084a82aea91ee65b075c04', '536b1067bea54944bb48f309c5bf2b98', NULL, '32655fdf23ab4bd9a0a1a4d8dbcf0b3d', NULL, '阶段四 Java Web核心(15)', NULL, NULL, '<p><strong>阶段四：Java Web核心</strong></p>\n<p><strong>本阶段将开启Servlet学习，重点学习Servlet内置对象、生命周期，要结合案例进行理解，先学会使用，再进行深入学习。</strong></p>\n<p><strong>第1天Servlet技术</strong></p>\n<pre>课程安排：<br /><br />1、常用关键字<br />2、命名规范<br />3、变量作用域<br />4、运算符<br />5、结构语句<br />6、Request和Response<br />7、Servlet生命周期<br />8、Servlet的体系<br />9、使用eclipse快速创建Servlet<br />10、Servlet的初始化参数<br />11、随服务初始化的Servlet<br />12、改造配件商城，使用Servlet实现<br />13、Servlet的注解</pre>\n<div class=\"text\">\n<p><strong>第2天 Filter技术</strong></p>\n<div class=\"week-name\">课程安排：</div>\n<div class=\"content\">\n<p>1、Filter简介<br />2、Filter的生命周期<br />3、工作原理（执行流程）<br />4、Url配置规则<br />5、HttpFilter的使用(重点&ndash;解决乱码问题)<br />6、Filter相关接口</p>\n<div class=\"text\">\n<p><strong>第3天Listener技术</strong></p>\n<div class=\"week-name\">课程安排：</div>\n<p>1、监听器简介<br />2、监听器分类&nbsp;<br />3、生命周期</p>\n</div>\n</div>\n</div>\n<div class=\"week-name\"><strong>第4天&nbsp; &nbsp;EL表达式和JSTL标签使用</strong></div>\n<div class=\"content\">\n<div class=\"desc\">本周的学习重点掌握EL。</div>\n<div class=\"black-title\">课程安排：</div>\n<div class=\"text\">1、EL表达式语法<br />2、JSTL标签<br />3、EL和JSTL项目应用</div>\n</div>\n<div class=\"week-name\"><strong>第5天 文件上传下载</strong></div>\n<div class=\"desc\">掌握IO文件读写操作，单个文件上传和多个文件上传</div>\n<div class=\"line\">&nbsp;</div>\n<div class=\"black-title\">课程安排：</div>\n<pre>1、File类<br />2、IO文件操作<br />3、单个文件的上传和下载<br />4、多个文件的上传和下载</pre>\n<div class=\"text\"><strong>第6天 验证码和防止表单重复提交实战案例</strong></div>\n<div class=\"text\">&nbsp;</div>\n<div class=\"text\"><strong>第7天 分页查询案例讲解和实战</strong></div>\n<div class=\"text\">&nbsp;</div>\n<div class=\"text\"><strong>第8天 多条件过滤查询讲解和实战</strong></div>', NULL, 4, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-03-19 21:18:45', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('f5e75a96bec4401496d414eebcb8f766', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '0b29170f3e5f491d90e44cbf01791763', 3, '阶段七 分布式技术核心(10)', NULL, NULL, '<div class=\"stage-section js-areatop js-section-0\">\n<div class=\"week-name\"><strong>阶段七：分布式核心技术</strong><hr /></div>\n<div class=\"week-name\">第1-5天&nbsp; &nbsp;Linux操作系统</div>\n<div class=\"content\">\n<div class=\"line\">&nbsp;</div>\n<div class=\"black-title\"><strong>课程安排：</strong></div>\n<div class=\"text\">1、linux基础入门<br />2、Linux系统管理（进阶）</div>\n<div class=\"text\">3、&nbsp;Linux Shell基础<br />3、Linux 常用命令<br />4、&nbsp;Linux下软件安装配置实战<br />5、Linux网络</div>\n<div class=\"text\">&nbsp;</div>\n</div>\n<div class=\"week-name\"><strong>第6天&nbsp; Zookeeper</strong><hr /></div>\n<div class=\"content\">\n<div class=\"desc\"><strong>课程安排：</strong></div>\n<div class=\"desc\">1、Zookeeper安装和使用</div>\n<div class=\"desc\">2、Zookeeper典型应用场景</div>\n<div class=\"desc\">分布式锁 ： 通过创建唯一节点获得分布式锁，当获得锁的一方执行完相关代码或者是挂掉之后就释放锁。<br />命名服务 ：可以通过 ZooKeeper 的顺序节点生成全局唯一 ID<br />数据发布/订阅 ：通过 Watcher 机制 可以很方便地实现数据发布/订阅。当你将数据发布到 ZooKeeper 被监听的节点上，其他机器可通过监听 ZooKeeper 上节点的变化来实现配置的动态更新。</div>\n<div class=\"desc\">3、连接 ZooKeeper 服务</div>\n<div class=\"desc\">4、Zookeeper常用命令</div>\n<div class=\"desc\">5、Zookeeper集群</div>\n<div class=\"desc\">6、ZAB 协议和Paxos 算法</div>\n<div class=\"text\">&nbsp;</div>\n</div>\n<div class=\"week-name\"><strong>第7天&nbsp; Dubbo</strong><hr /></div>\n<div class=\"content\">\n<div class=\"text\">\n<div class=\"desc\"><strong>课程安排：</strong></div>\n<div class=\"desc\">1、何为 RPC?<br />2、RPC 的原理是什么?<br />3、什么是 Dubbo?<br />4、为什么要用 Dubbo?<br />5、什么是分布式?<br />6、为什么要分布式?<br />7、Dubbo 架构<br />8、Dubbo 架构中的核心角色有哪些？<br />9、Dubbo 架构中的核心角色有哪些？<br />10、Dubbo 的工作原理了解么？<br />11、Dubbo 的 SPI 机制了解么？ 如何扩展 Dubbo 中的默认实现？<br />12、Dubbo 的微内核架构了解吗？<br />13、Dubbo 的微内核架构了解吗？<br />14、Dubbo序列化协议</div>\n</div>\n</div>\n<div class=\"week-name\"><strong>第8天&nbsp; Redis</strong><hr /></div>\n<div class=\"content\">\n<div class=\"text\">\n<div class=\"desc\"><strong>课程安排：</strong></div>\n<div class=\"desc\">1、Redis简介</div>\n<div class=\"desc\">2、说一下 Redis 和 Memcached 的区别和共同点</div>\n<div class=\"desc\">3、缓存数据的处理流程是怎样的？</div>\n<div class=\"desc\">4、为什么要用 Redis/为什么要用缓存？</div>\n<div class=\"desc\">5、Redis 常见数据结构以及使用场景分析</div>\n<div class=\"desc\">6、Redis 单线程模型详解</div>\n<div class=\"desc\">7、Redis 给缓存数据设置过期时间</div>\n<div class=\"desc\">8、Redis 内存淘汰机制了解么？</div>\n</div>\n</div>\n<div class=\"week-name\"><strong>第9天&nbsp; Nginx</strong><hr /></div>\n<div class=\"content\">\n<div class=\"text\">\n<div class=\"desc\"><strong>课程安排：</strong></div>\n<div class=\"desc\">1、Nginx安装和配置</div>\n<div class=\"desc\">2、Nginx常用命令使用</div>\n<div class=\"desc\">3、Nginx如何实现静态资源缓存</div>\n<div class=\"desc\">4、Nginx如何实现反向代理</div>\n<div class=\"desc\">5、Nginx如何实现高可用集群</div>\n<div class=\"desc\">6、Nginx如何实现https安全通信</div>\n<div class=\"desc\">&nbsp;</div>\n</div>\n</div>\n</div>\n<div class=\"stage-section js-areatop js-section-1\">\n<div class=\"top\">\n<div class=\"top\">&nbsp;</div>\n</div>\n<div class=\"top\">&nbsp;</div>\n<div class=\"top\">&nbsp;</div>\n<div class=\"top\">&nbsp;</div>\n<div class=\"top\">&nbsp;</div>\n</div>', NULL, 6, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('f614e92231924a3cb9db082870388658', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '8487eebcb5cb4cfd9ff551c79046a449', 1, '来校路线', NULL, NULL, '<p><iframe style=\"width: 560px; height: 362px;\" src=\"https://www.budaos.com/static/creatorblue/tinymce/bdmap/bd.html?center=112.82928842683339%2C28.35027244428658&amp;zoom=19&amp;width=558&amp;height=360\" frameborder=\"0\">​</iframe></p>\n<p><strong>教学实训基地：长沙市望城区新塘路199号创蓝信息（望城移动分公司后)</strong></p>\n<p><strong>联系人：小刘老师</strong></p>\n<p><strong>联系电话：15886345118</strong></p>\n<p><strong>来校公校：W216/W131/W151/903路</strong></p>', NULL, 3, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('f6bca9a568394c8f9850f140f4d1a33f', '8487eebcb5cb4cfd9ff551c79046a449', NULL, '25d0cdb6db744f8c8fa4039f1f90049c', 2, '共研教材', NULL, NULL, '<p>线下教材(专升本纸质教材)</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/9eda5f514b584326a41f8838680676a3.jpg\" alt=\"\" width=\"1015\" height=\"540\" /></p>\n<p>IT类培训（线上活教材）与合作企业共同研发教材，教材每月升级版本，及时与企业技术同步和调整，线上活教材：图文、视频、音频更丰富</p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-14/8f547bcedbb34411a091be22a957263d.png\" /></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/3c317a70a10a4d0a9cfae2ee3978104d.jpg\" alt=\"\" width=\"1000\" height=\"659\" /></p>', NULL, 2, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-06-15 21:10:59', NULL, NULL, 'Y', NULL);
INSERT INTO `t_evgl_book_chapter` VALUES ('f75fd5341d8d42bc85e2b03d96130854', '536b1067bea54944bb48f309c5bf2b98', NULL, '8be4a934bf22415d87ad3f81075ae5f3', NULL, '实训六 编码实现', NULL, NULL, '<p>源代码仓库地址：<a href=\"http://admin@git.creatorblue.com/r/contactitem/odc/odc-admin.git\" target=\"_blank\" rel=\"noopener\">http://admin@git.creatorblue.com/r/contactitem/odc/odc-admin.git</a></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-20/731f1f94ecfa48188d315b486a7dfd39.png\" width=\"822\" height=\"429\" /></p>\n<p>1、源码级分析底层平台，弄清楚各模块间的调用关系，为模块开发做准备</p>\n<p>2、通过分析行业流行框架源码，熟练使用项目中如何灵活应用</p>\n<p>3、弄清楚平台的权限RBAC是如何管理和设计</p>\n<p>4、角色如何设置，角色权限如何设计</p>\n<p>5、熟悉平台后，进行模块开发</p>\n<p>6、熟练使用Junit进行单元测试</p>', NULL, 8, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-04-19 17:40:56', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('f84907910dff42c5bd1845474602eb8f', '536b1067bea54944bb48f309c5bf2b98', NULL, 'c59b05c081b54bbe9132dd88ac2a651a', NULL, '企业级网络/防火墙实战', NULL, NULL, '<div class=\"week-name\"><strong>第1章 网络安全-企业防火墙</strong><br /><hr /></div>\n<div class=\"content\">\n<div class=\"line\">简介：安全在互联网的中越来越受到企业和boss们的重视，本课给大家介绍一些网络扫描的技术手段，其中包括主机扫描、路由扫描、批量服务扫描。同时我们也会了解什么是恶意扫描行为，在实际工作中如何合理设置利用基于Linux的内核参数或者利用相关的工具规避一些安全的隐患。</div>\n<div class=\"black-title\"><strong>课程安排：</strong></div>\n<div class=\"text\">1、linux扫描技术概述<br />2、网络安全技术定义<br />3、fping应用</div>\n<div class=\"text\">&nbsp; 3.1 fping概述及大海源码包编译原理</div>\n<div class=\"text\">&nbsp; 3.2 通过源码包方式安装fping</div>\n<div class=\"text\">&nbsp; 3.3 fping参数介绍</div>\n<div class=\"text\">&nbsp; 3.4 fping参数终端演示<br />4、hping应用</div>\n<div class=\"text\">&nbsp; &nbsp;4.1&nbsp;hping介绍及安装</div>\n<div class=\"text\">&nbsp; &nbsp;4.2 hping参数介绍</div>\n<div class=\"text\">5、路由扫描</div>\n<div class=\"text\">&nbsp; 5.1&nbsp;路由扫描介绍</div>\n<div class=\"text\">&nbsp; 5.2 traceroute参数使用</div>\n<div class=\"text\">&nbsp; 5.3 mtr使用</div>\n<div class=\"text\">6、批量主机扫描</div>\n<div class=\"text\">&nbsp; &nbsp;6.1&nbsp;批量主机服务扫描</div>\n<div class=\"text\">&nbsp; &nbsp;6.2 nmap介绍</div>\n<div class=\"text\">&nbsp; &nbsp;6.3 ncat使用介绍</div>\n<div class=\"text\">7、防攻击介绍</div>\n<div class=\"text\">&nbsp;</div>\n<div class=\"text\">\n<div class=\"week-name\"><strong>第2章 iptables防护</strong><br /><hr /></div>\n<div class=\"content\">\n<div class=\"line\">\n<div class=\"text\">简介：在&rdquo;黑与白&ldquo;的新型互联网时代，不经意间你的站点就可能被一个买地瓜的小朋友标记上了&ldquo;俺到此地一游！&rdquo;。安全已经成了互联网、物联网革命时代必须关注并且掌握的一门技术。本课程围绕Linux的iptables为您从简到繁的介绍，如何用iptables守护好您家的前门和后院。</div>\n</div>\n<div class=\"black-title\"><strong>课程安排：</strong></div>\n<div class=\"text\">1、iptables简介<br />2、通过iptables控制并发访问场景</div>\n<div class=\"text\">3、iptables规则原理和组成</div>\n<div class=\"text\">&nbsp; 3.1 netfilter和iptables</div>\n<div class=\"text\">&nbsp; 3.2&nbsp; iptables的四张表和5条链</div>\n<div class=\"text\">&nbsp; 3.3 数据包在4张表和5条链的流向</div>\n<div class=\"text\">&nbsp; 3.4 iptables场景二&nbsp;ftp被动模式</div>\n<div class=\"text\">&nbsp; 3.5 iptables中NAT模式设置</div>\n<div class=\"text\">&nbsp; 3.6 iptables防攻击及企业场景应用：防止CC攻击</div>\n<div class=\"text\">&nbsp;</div>\n</div>\n</div>\n<div class=\"text\">\n<div class=\"week-name\"><strong>第3章 Linux系统下智能DNS服务搭建</strong><br /><hr /></div>\n<div class=\"content\">\n<div class=\"line\">\n<div class=\"text\">首先介绍了DNS的原理和应用，然后自动动手搭建Bind服务，并进行测试，最后还介绍了DNS正向解析工具的使用。</div>\n</div>\n<div class=\"black-title\"><strong>课程安排：</strong></div>\n<div class=\"text\">1、Bind介绍<br />2、DNS中的域名</div>\n<div class=\"text\">3、域名解析及权威域名解析</div>\n<div class=\"text\">4、域名解析记录分类</div>\n<div class=\"text\">5、Bind服务安装和配置</div>\n<div class=\"text\">6、Bind服务实战场景</div>\n<div class=\"text\">7、DNS正向解析与反向解析</div>\n<div class=\"text\">8、DNS正向解析客户端工具</div>\n</div>\n</div>\n</div>', NULL, 11, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-04-12 11:40:53', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('f9c43e2accbd4d63b5e94dc9415b3554', '536b1067bea54944bb48f309c5bf2b98', NULL, '83bcb90aed2849f19728665a42bcd689', NULL, '任务描述', NULL, NULL, '<p><strong>实战技能&nbsp; 如何快速生成百万级数据</strong></p>\n<table style=\"height: 86px; width: 100%; border-collapse: collapse;\" border=\"1\">\n<tbody>\n<tr style=\"height: 19px;\">\n<th style=\"width: 35.0209%; height: 19px; background-color: #3b35f0;\"><span style=\"color: #ffffff;\">任务名称</span></th>\n<th style=\"width: 49.9443%; height: 19px; background-color: #3b35f0;\"><span style=\"color: #ffffff;\">快速生成数据库设计文档</span></th>\n</tr>\n<tr style=\"height: 19px;\">\n<td style=\"width: 35.0209%; height: 19px;\">任务要求</td>\n<td style=\"width: 49.9443%; height: 19px;\">\n<p>1、以数据库高级功能函数、存储过程生成百万数据</p>\n<p>2、学会搭建maven工程</p>\n<p>3、学会如何高效使用maven并管理项目</p>\n<p>4、学会使用maven插件</p>\n</td>\n</tr>\n<tr style=\"height: 19px;\">\n<td style=\"width: 35.0209%; height: 19px;\">任务内容</td>\n<td style=\"width: 49.9443%; height: 19px;\">\n<p>1、遇到依赖包下载失败时，如何来处理</p>\n<p>2、跳过maven单元测试如何来实现</p>\n<p>3、Maven镜像如何更换</p>\n<p>4、Maven如何与eclipse、VScode工具集成和配置</p>\n</td>\n</tr>\n<tr style=\"height: 19px;\">\n<td style=\"width: 35.0209%; height: 10px;\">解决问题</td>\n<td style=\"width: 49.9443%; height: 10px;\">\n<p>1、生成html和doc文档解决团队数据库开发间的协作问题</p>\n<p>2、解决系统验收时，数据库设计文档的编写难的问题</p>\n<p>3、解决项目间文档的规范问题</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr style=\"height: 19px;\">\n<td style=\"width: 35.0209%; height: 19px;\">提交内容</td>\n<td style=\"width: 49.9443%; height: 19px;\">&nbsp;</td>\n</tr>\n</tbody>\n</table>', NULL, 2, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-05-25 16:48:48', NULL, NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('fbb6e88195574a77a40424b1d5724970', '536b1067bea54944bb48f309c5bf2b98', NULL, '8be4a934bf22415d87ad3f81075ae5f3', NULL, '项目要求', NULL, NULL, '<p>主要训练学员对于实际开发项目的开发能力。</p>\n<p>1、学会使用思维导图梳理E-R图</p>\n<p>2、学会进行项目的需求分析</p>\n<p>3、掌握需求分析的方法和工具</p>\n<p>4、熟悉软件开发的生命周期和流程</p>\n<p>5、熟练使用软件开发工具Intellij IDEA,VSCode,Maven</p>\n<p>6、学会灵活运用应用服务器：Tomcat和Jetty</p>\n<p>7、学会使用建模工具进行数据库设计</p>\n<p>8、学会软件测试常用的方法和开发调试方法和技巧等</p>\n<p>9、培养团队精神，注重团队协作能力</p>\n<p>10、学会遇到问题，快速解决问题的能力</p>', NULL, 2, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-04-20 09:57:11', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');
INSERT INTO `t_evgl_book_chapter` VALUES ('fcbe9668a72a40c7a6322c23403acc9e', '536b1067bea54944bb48f309c5bf2b98', NULL, '32655fdf23ab4bd9a0a1a4d8dbcf0b3d', NULL, '阶段十 企业级项目实战(90)', NULL, NULL, '<p><span style=\"line-height: 1.5;\">学员具备了Java面向对象编程相关知识与技术，掌握了Java相关平台技术的基础上，着重培养学员的实际应用系统能力、分析问题与解决问题的能力以及创新力。</span></p>\n<p><span style=\"line-height: 1.5;\">项目实战周期为二个月，可根据培训进度情况适时调整。</span></p>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-02-05/627db0413acd41f1a5ec9bb07ec91d26.png\" width=\"1084\" height=\"426\" /></p>\n<table style=\"border-collapse: collapse; width: 100%; height: 163px;\" border=\"1\">\n<tbody>\n<tr style=\"height: 16px; border-style: solid; background-color: #e8dcdc;\">\n<td style=\"width: 11.4243%; height: 16px; background-color: #5b34eb;\"><strong>序号</strong></td>\n<td style=\"width: 18.9768%; height: 16px; background-color: #5b34eb;\"><strong>项目名称</strong></td>\n<td style=\"width: 8.04571%; height: 16px; background-color: #5b34eb;\"><strong>项目周期</strong></td>\n<td style=\"width: 61.5532%; height: 16px; background-color: #5b34eb;\"><strong>备注</strong></td>\n</tr>\n<tr style=\"height: 17px;\">\n<td style=\"width: 11.4243%; height: 17px;\">1</td>\n<td style=\"width: 18.9768%; height: 17px;\">名车门户项目</td>\n<td style=\"width: 8.04571%; height: 17px;\">15天</td>\n<td style=\"width: 61.5532%; height: 17px;\">\n<p>中高端企业网站建设</p>\n<p>基于SSM框架实现</p>\n</td>\n</tr>\n<tr style=\"height: 17px;\">\n<td style=\"width: 11.4243%; height: 17px;\">2</td>\n<td style=\"width: 18.9768%; height: 17px;\">互联网+餐饮</td>\n<td style=\"width: 8.04571%; height: 17px;\">20天</td>\n<td style=\"width: 61.5532%; height: 17px;\">\n<p>前端多终端支持(手机、平板、PC端)+后台管理系统(后端)</p>\n<p>前后端分离、多模块平台</p>\n</td>\n</tr>\n<tr style=\"height: 17px;\">\n<td style=\"width: 11.4243%; height: 17px;\">3</td>\n<td style=\"width: 18.9768%; height: 17px;\">客服管理系统</td>\n<td style=\"width: 8.04571%; height: 17px;\">15天</td>\n<td style=\"width: 61.5532%; height: 17px;\">\n<p>通讯中台TIO应用和微服务架构综合应用</p>\n<p>基于微信小程序</p>\n</td>\n</tr>\n<tr style=\"height: 48px;\">\n<td style=\"width: 11.4243%; height: 48px;\">4</td>\n<td style=\"width: 18.9768%; height: 48px;\">优购电子商务平台管理端</td>\n<td style=\"width: 8.04571%; height: 48px;\">15天</td>\n<td style=\"width: 61.5532%; height: 48px;\">\n<p>电子商务类</p>\n</td>\n</tr>\n<tr style=\"height: 48px;\">\n<td style=\"width: 11.4243%; height: 48px;\">5</td>\n<td style=\"width: 18.9768%; height: 48px;\">户外通微信小程序项目</td>\n<td style=\"width: 8.04571%; height: 48px;\">20天</td>\n<td style=\"width: 61.5532%; height: 48px;\">\n<p>户外运动APP</p>\n</td>\n</tr>\n</tbody>\n</table>', NULL, 10, 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2021-03-24 15:56:56', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', NULL, 'Y', 'Y');

