INSERT INTO `t_evgl_book_major` VALUES ('1cd2c3ccaf814e04a2792da827ca91ed', '845786f2bfbf46398e3b495f6c7014bc', '前端工程师', '522b23b2-6f28-4465-b2d4-5234f3287ed5.png', 'IT界的时尚达人\nWeb 前端学习路径中首先实践 HTML、CSS、JavaScript、CSS3、HTML5、熟练的图像处理能力等基础知识，然后完成实现页面特效组件、网页游戏、Web应用等一系列项目，帮助你通过动手实践成长为合格的 Web 前端工程师。', '<p><img src=\"../manage/uploads/cbeditor-image/2019-08-16/dfe6a13296474135aeaa7d4dab05478a.png\" width=\"1188\" height=\"239\" /></p>\n<p><img src=\"../manage/uploads/cbeditor-image/2019-08-16/52cd2f9e6ec84c7aae29264a35e67418.png\" width=\"1190\" height=\"545\" /></p>\n<p><img src=\"../manage/uploads/cbeditor-image/2019-08-16/a9e1fa67d9594108820e2d7b0235bf0f.png\" width=\"1191\" height=\"440\" /><img src=\"../manage/uploads/cbeditor-image/2019-08-16/cf969724534a4528a02726d45f7c29a8.png\" width=\"1187\" height=\"407\" /></p>', '', '', '', 'V1.0', '', 'Y', 3, '1', '2019-08-16 19:20:20', '1', '2020-11-13 19:41:14', 'Y', 'Y');
INSERT INTO `t_evgl_book_major` VALUES ('80aa274044704360901836e9c7dda760', '845786f2bfbf46398e3b495f6c7014bc', '企业公开课', '4ba42aa4-5ba1-4d5f-8e54-60f64f27e16f.jpeg', '大数据学习路径中会学习并实践 Java、Scala、Hadoop、HBase、Mahout、Sqoop及Spark等大数据技术，本路径通过大量的动手实验，在实验数据集上实践各种大数据工具，帮助你成长为具备动手能力的大数据工程师。', '<p>大数据学习路径中会学习并实践 Java、Scala、Hadoop、HBase、Mahout、Sqoop及Spark等大数据技术，本路径通过大量的动手实验，在实验数据集上实践各种大数据工具，帮助你成长为具备动手能力的大数据工程师。</p>', '', '', '', 'V1.0', '', 'Y', 3, '1', '2019-08-16 20:43:17', '1', '2021-08-14 08:52:52', 'Y', 'Y');
