INSERT INTO `t_evgl_pkg_res` VALUES ('6ca8c075c88b414091807df40267c1ad', '44a79712b73f4c949a13c25edff1a2cc', 'a6039e4d946a4e838c007fd56320957c', NULL, '<div class=\"week-name\"><strong>阶段九：数据结构算法 </strong><hr /></div>\n<div class=\"week-name\">第1-5天&nbsp; 数据结构</div>\n<div class=\"content\">\n<div class=\"line\">&nbsp;</div>\n<div class=\"black-title\"><strong>课程安排：</strong></div>\n<div class=\"text\">1、数据结构<br />2、JVM优化</div>\n<div class=\"text\">3、Java并发<br />3、Java多线程<br /><br /></div>\n<div class=\"text\">\n<div class=\"week-name\">第6-10天&nbsp; MySQL数据库优化</div>\n<div class=\"content\">\n<div class=\"line\">&nbsp;</div>\n<div class=\"black-title\"><strong>课程安排：</strong></div>\n<div class=\"text\">1、数据库索引优化<br />2、数据库存储过程优化</div>\n<div class=\"text\">3、函数优化<br />4、视图优化</div>\n<div class=\"text\">5、SQL脚本优化</div>\n</div>\n</div>\n<div class=\"text\">&nbsp;</div>\n</div>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2021-04-12 09:35:47', NULL, NULL);
INSERT INTO `t_evgl_pkg_res` VALUES ('6e3b0de9f176415087bbd919ae0f251d', 'a2756d32388e411190883a493dbabbe8', 'c2b35915677342deafa82afe5981c1e0', NULL, '<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/384e838f00ab4481a35aed64fc447153.jpg\" alt=\"\" width=\"997\" height=\"574\" /></p>\n<h3 class=\"tit2\"><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-03-31/53de79bc6c6746e5943d6bdd76b795d2.jpg\" alt=\"\" width=\"867\" height=\"491\" /></h3>\n<h3 class=\"tit2\">高强度严苛训练 6个月让你涅盘重生</h3>\n<p><img src=\"https://www.budaos.com/bds//uploads/cbeditor-image/2021-04-14/987b78662018450b85dd68d8acf5b7e4.png\" /></p>\n<h5><strong><span style=\"font-size: 16px;\">每日通关</span></strong></h5>\n<p>每天设置专项练习、评测或小项目内容，让每位学员每天都能感受到自己的进步。</p>\n<h5><span style=\"font-size: 16px;\">敏捷开发</span></h5>\n<p>快速学习应用敏捷方法，能够学会做单团队单迭代及多团队多迭代的敏捷开发，提倡自我总结和改进。</p>\n<h5><span style=\"font-size: 16px;\">全程项目</span></h5>\n<p>以项目驱动反向推动学习，让每个知识点技能点都能学以致用，使学员的学习更具方向性。</p>\n<h5><span style=\"font-size: 16px;\">阶段考核</span></h5>\n<p>学习过程采用阶段考核考试，螺旋式成长，严格学好每门课，不抛弃每个深度挖掘每个学员潜能。</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2021-06-15 21:10:59', NULL, '2021-06-10 09:17:18', NULL, NULL);
