INSERT INTO `t_evgl_site_update_log` VALUES ('16f6e97ae58c426393abe725d1e9fdbc', '2', 'V4.0.0', '<p>1、新增:&nbsp; 直播功能</p>\n<p>2、新增：我要咨询与企业微信信息推送互通</p>\n<p>3、新增：报名功能</p>\n<p>4、新增：微信支付功能</p>\n<p>5、优化：修复部分Bug</p>', '1', '2021-11-08 16:02:18', '1', '2021-11-08 16:02:39');
INSERT INTO `t_evgl_site_update_log` VALUES ('584bd863df5b4614ba002adcdfd5628a', '2', 'V3.0.0', '<p>1、新增:&nbsp; 云盘功能</p>\n<p>2、新增：一键入课堂、批量审核</p>\n<p>3、新增：我要测评，实践考核、测试活动</p>\n<p>4、新增：在线考试、智能阅卷</p>\n<p>5、优化：修复部分Bug</p>\n<p>6、优化：体验性和安全性提升</p>', '1', '2020-04-17 18:04:30', '1', '2021-11-08 16:19:01');
INSERT INTO `t_evgl_site_update_log` VALUES ('9a3086b115f84ebd9b805eef588617e5', '2', 'V2.0.1', '<p>1、新增:&nbsp; 博客板块</p>\n<p>2、新增：成员分组和成员角色划分功能</p>\n<p>3、新增：辅教视频库功能</p>\n<p>4、优化：修复部分Bug</p>\n<p>5、优化：教学包版本和审核功能</p>', '1', '2019-03-01 19:48:00', '1', '2021-09-12 19:52:59');
INSERT INTO `t_evgl_site_update_log` VALUES ('b15a8b9fcf1141d9bb454f70b777a6dd', '2', 'V1.0.1', '<p>1、新增：新建课堂</p>\n<p>2、新增：课堂活动：签到、课堂表现、头脑风暴、答疑/讨论、投票/问卷</p>\n<p>2、新增：二维码扫码加课堂</p>\n<p>3、新增：填写邀请码加入课堂</p>\n<p>4、优化：修复部分Bug</p>\n<p>5、优化：界面支持保护色</p>\n<p>6、优化：修改底部菜单样式</p>', '1', '2018-09-23 11:06:06', '1', '2021-11-08 16:18:44');

