INSERT INTO `t_evgl_tch_class` VALUES ('2b222feef75f41d9b6f2aa5e1f7e3000', 'd9fbce2fba844ae6b34f69ce2278f191', 'fd5f006eacee4aafab29248649af2a92', '', '2天带你轻松入门Linux云计算', '8b53b0a9-e579-4591-a192-c5deb5b4b194.jpeg', NULL, NULL, NULL, '1', '2021-09-25 16:21:09', '1', '2022-05-09 17:45:05', '', '3', '2022-05-15', '2022-05-15', NULL, NULL, 20, '<p><strong>5天轻松入门Linux云计算</strong></p>\n<p><img src=\"https://www.budaos.com/manage/uploads/cbeditor-image/2022-02-09/829c6c60442a4d2ba0cf425d1ebbaa2b.png\" width=\"977\" height=\"415\" /></p>\n<p><img src=\"https://www.budaos.com/manage/uploads/cbeditor-image/2022-02-09/c1763d600a2a4491b5cd886dc54ee260.png\" width=\"977\" height=\"381\" /></p>\n<p><img src=\"https://www.budaos.com/manage/uploads/cbeditor-image/2022-02-09/59d20afe15854caa840b46d16fac9626.png\" width=\"983\" height=\"339\" /></p>\n<p>包括十个章节，50个实战任务。</p>\n<p><img src=\"https://www.budaos.com/manage/uploads/cbeditor-image/2022-02-09/93e417107a1f40daa55b05d3909c1a47.png\" width=\"984\" height=\"569\" /></p>\n<p><img src=\"https://www.budaos.com/manage/uploads/cbeditor-image/2022-02-09/d5cd68d46db44b5bb27ae746a89dfd0c.png\" width=\"969\" height=\"580\" /></p>', 'e0eab2cde5114852b93c06b5bb41a797', '1', 'e4956a0c5e7449aeb8bb2ca5cd862c4b', '2022-05-03', '/uploads/class-img/7b627df5-3656-475a-a9f3-11be69d8f516.png');
