INSERT INTO `t_evgl_tch_teacher` VALUES ('1a9a33b872aa48bcbb2141f35217ce00', '845786f2bfbf46398e3b495f6c7014bc', '小强老师', 'e1c262a5b17b4ec698ca18e16646b53f', '9f500494-133b-4ffb-8474-a05e6a54eea4.jpeg', '十余年Java EE 开发和项目管理经验，横跨前后台开发，软件研发多面手。精通Java,擅长云架构和微服务。目前主要负责微服务技术推广、架构设计、基础设施、迁移等。重点关注云计算、微服务以及软件架构等领域。', '<p>项目经验：曾任职于中国电信、当当网、中软等大型互联网公司。曾参与的项目有湖南移动商城、阿凡提物流平台、企业办公自动化OA、步步高电商平台等项目建设。</p>', '', '	13297482107', '1a9a33b872aa48bcbb2141f35217ce00', 'd9fbce2fba844ae6b34f69ce2278f191', 'Y', '1', '2019-08-17 11:39:41', '1', '2019-08-17 15:59:26', 'Y', 6, NULL, NULL, NULL, NULL);
INSERT INTO `t_evgl_tch_teacher` VALUES ('278174c1a0024770aad1429d7dc842e9', '845786f2bfbf46398e3b495f6c7014bc', '欧老师', '490aa2321c5f42fcbfb8fcc5a1f39bd5', '33cbc3f8026b43c7a5c087b3f43c22f6.jpeg', '16年一线IT经验及教学培训经验，创蓝教育教学总监，教学幽默风趣，坚持授人与鱼不如授人与渔的教学理念，注重培养学生解决问题与自我思考的能力。 讲课风格：课堂不仅仅是编程，还融合了人生，哲理，社交，为人处世，段子等各种知识，让学生不仅学会编程，还懂得如何在社会中更好的生存。', '<p>项目经验：多年大型软件企业软件开发和项目管理经验，曾参与国家电网数据库联网项目、广州亚运智能指挥调度系统、ERP企业资源计划管理、广东质监计财系统等项目。</p>', '', '1507830712', '278174c1a0024770aad1429d7dc842e9', 'd9fbce2fba844ae6b34f69ce2278f191,1cd2c3ccaf814e04a2792da827ca91ed', 'Y', '1', '2019-08-17 11:33:13', '1', '2020-12-20 15:19:46', 'Y', 3, 'a645cf35f68f4620a3d3753677dd98d3', '20098899909011155', '20199900000333', '1');
