INSERT INTO `t_evgl_tch_teacher` VALUES ('350674e438e14598a96cec4e8167cb0b', '845786f2bfbf46398e3b495f6c7014bc', '张文君', 'e1c262a5b17b4ec698ca18e16646b53f', '00b9e5a3-1b87-44b5-b490-671a1b695108.jpeg', '专注于项目测试和技能培训。多年安全测试以及测试管理工作经验，曾担任自动化测试工程师、安全测试工程师和测试总监等职位。在系统安全、性能、功能性测试领域积累了丰富的经验并取得丰硕成果。', '<p>项目经验：曾就职于中软国际、科创等多家上市公司。进行过多个大型项目的Web安全测试，带领测试部门从零开始建立安全性测试规范，针对Web安全测试搭建了基于开源工具的安全测试架构，并应用于教育大数据平台和金融征信平台项目。</p>', '', '18874237001', '002', '1c5e7a77b9c94f1688b81e2f9739b6f2,80aa274044704360901836e9c7dda760', 'Y', '1', '2019-08-17 11:38:38', '1', '2019-08-17 15:59:20', 'Y', 5, NULL, NULL, NULL, NULL);
