INSERT INTO `t_evgl_tch_teacher` VALUES ('3b36c777604241879108c055af34fff4', '845786f2bfbf46398e3b495f6c7014bc', '苏老师', 'e1c262a5b17b4ec698ca18e16646b53f', '4864fe59-b339-4d93-8f4d-4dd1936f751e.jpeg', '10年UI设计和开发经验，创蓝首席UI设计师，对UI交互性设计、体验性设计有自己独到的见解和理念 。精通响应式框架设计，企业徽标设计、专注公众号、小程序、APP制作、网站、门户、后台管理UI制作 等。', '<p>项目经验：专注移动产品及智能交互终端的UI设计；曾参与多个知名企业的大型平台项目的视觉设计、多款企业内部产品的APP设计等。</p>', '', '15999610130', '', '1cd2c3ccaf814e04a2792da827ca91ed', 'Y', '1', '2019-08-17 11:37:05', '1', '2019-08-17 15:59:17', 'Y', 4, NULL, NULL, NULL, NULL);
