INSERT INTO `t_evgl_tch_teacher` VALUES ('9f52397c7c0f4627839294fba7ff1fda', '845786f2bfbf46398e3b495f6c7014bc', '卢老师', 'e1c262a5b17b4ec698ca18e16646b53f', 'a3aaac22-c907-4b5a-bf01-54de57e14c7d.jpeg', '有丰富的设计从业经验，涉及插画图书、动画、游戏、应用设计等领域。有深厚的手绘功底和丰富的设计理论知识，且具有很强的视觉设计能力和丰富的交互设计经验。从事设计工作8年间参与过各种类型项目的设计工作，包括动画设计、游戏美术设计、UI设计、交互设计。', '<p>项目经验：曾在中国移动、华为、中国电信、浪潮等公司担任team leader，并有5年以上的设计管理经验和3年项目管理经验。</p>', '', '13786199187', '003', '1cd2c3ccaf814e04a2792da827ca91ed', 'Y', '1', '2019-08-17 11:35:53', '1', '2019-08-17 15:58:57', 'Y', 2, NULL, NULL, NULL, NULL);
