select ifnull(max(sort_num), 0) + 1 from T_EVGL_PKG_RESGROUP
and pkg_id =#{pkgId,jdbcType=VARCHAR}
and subject_id =#{subjectId,jdbcType=VARCHAR}
and chapter_id =#{chapterId,jdbcType=VARCHAR}
and resgroup_name =#{resgroupName,jdbcType=VARCHAR}
and parent_id =#{parentId,jdbcType=VARCHAR}
and res_type =#{resType,jdbcType=VARCHAR}
and sort_num =#{sortNum,jdbcType=INTEGER}
and resgroup_total =#{resgroupTotal,jdbcType=INTEGER}
and create_user_id =#{createUserId,jdbcType=VARCHAR}
and create_time =#{createTime,jdbcType=VARCHAR}
and update_user_id =#{updateUserId,jdbcType=VARCHAR}
and update_time =#{updateTime,jdbcType=VARCHAR}
and state =#{state,jdbcType=VARCHAR}
and group_type =#{groupType,jdbcType=VARCHAR}

select t1.resgroup_id as resgroupId, t1.pkg_id as pkgId, t1.chapter_id as chapterId, t1.resgroup_name as resgroupName, t1.sort_num as sortNum, ifnull(t1.dict_code, '') as dictCode, t2.res_id as resId, ifnull(t2.res_content, '') as resContent from T_EVGL_PKG_RESGROUP t1 inner join t_evgl_pkg_res t2 on t1.resgroup_id = t2.resgroup_id
and t1.chapter_id in
#{chapterId,jdbcType=VARCHAR}
and t2.resgroup_id =#{resgroupId,jdbcType=VARCHAR}
and t1.ct_id =#{ctId,jdbcType=VARCHAR}
and t1.pkg_id =#{pkgId,jdbcType=VARCHAR}
and t1.subject_id =#{subjectId,jdbcType=VARCHAR}
and t1.chapter_id =#{chapterId,jdbcType=VARCHAR}
and t1.resgroup_name =#{resgroupName,jdbcType=VARCHAR}
and t1.parent_id =#{parentId,jdbcType=VARCHAR}
and t1.res_type =#{resType,jdbcType=VARCHAR}
and t1.sort_num =#{sortNum,jdbcType=INTEGER}
and t1.resgroup_total =#{resgroupTotal,jdbcType=INTEGER}
and t1.create_user_id =#{createUserId,jdbcType=VARCHAR}
and t1.create_time =#{createTime,jdbcType=VARCHAR}
and t1.update_user_id =#{updateUserId,jdbcType=VARCHAR}
and t1.update_time =#{updateTime,jdbcType=VARCHAR}
and t1.state =#{state,jdbcType=VARCHAR}
and t1.flag =#{flag,jdbcType=VARCHAR}
and t1.group_type =#{groupType,jdbcType=VARCHAR}
order by ${sidx} ${order}
order by t1.resgroup_id desc

select ifnull(t4.is_trainees_visible, 'Y') as isTraineesVisible, t4.rv_id as rvId, ifnull(t3.is_can_copy, 'Y') as isCanCopy, t3.cp_id as cpId, t3.pkg_id, t1.resgroup_id as resgroupId, t1.pkg_id as pkgId, t1.chapter_id as chapterId, t1.resgroup_name as resgroupName, t1.sort_num as sortNum, ifnull(t1.dict_code, '') as dictCode, t2.res_id as resId, ifnull(t2.res_content, '') as resContent from T_EVGL_PKG_RESGROUP t1 inner join t_evgl_pkg_res t2 on t1.resgroup_id = t2.resgroup_id left join t_evgl_pkg_resgroup_allow_copy t3 on t1.resgroup_id = t3.resgroup_id and t3.pkg_id = #{ctPkgId} left join t_evgl_pkg_resgroup_visible t4 on t1.resgroup_id = t4.resgroup_id and t4.pkg_id = #{ctPkgId}
and (t1.pkg_id =#{pkgId,jdbcType=VARCHAR} or t1.subject_id =#{subjectId,jdbcType=VARCHAR})
and t1.chapter_id =#{chapterId,jdbcType=VARCHAR}
order by ${sidx} ${order}
order by t1.resgroup_id desc

